INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10494,'paragraph',NULL,'A final comment is in order about the limits of summation in <a href="eq:dft3" class="ITS_reference">&raquo;</a>.  We have chosen to sum over <span class="ITS_HTML">N</span> samples of <span class="ITS_HTML">x[n]</span> and to evaluate the DFT at <span class="ITS_HTML">N</span> frequencies <span class="ITS_HTML">&omega;<sub>k</sub>=2&pi; k/N</span>. Often, the sequence <span class="ITS_HTML">x[n]</span> has length <span class="ITS_HTML">L</span> such that <span class="ITS_HTML">L<N</span>. In such cases, we simply append <span class="ITS_HTML">N\!-\!L</span> zero samples to the nonzero samples of <span class="ITS_HTML">x[n]</span> in carrying out the computation. If, in fact, <span class="ITS_HTML">x[n]</span> is nonzero only in the interval <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?0\\leq n \\leq \\Lmone"/>, then appending zero samples is exactly the right thing to do.','13','10','8');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10495,'equation','none','\\begin{align*} X[0] &= x[0]e^{-j0} + x[1]e^{-j0} + x[2]e^{-j0} + x[3]e^{-j0}\\\\ &= 1 + 1 + 0 + 0 = 2\\\\ X[1] &= x[0]e^{-j0} + x[1]e^{-j\\pi/2} + 0e^{-j\\pi} +0e^{-j3\\pi/2}\\\\ &= 1 + (-j) + 0 + 0 = 1-j = \\sqrt{2} e^{-j\\pi/4}\\\\ X[2] &= x[0]e^{-j0} + x[1]e^{-j\\pi} + 0e^{-j2\\pi} + 0e^{-j3\\pi}\\\\ &= 1 + (-1) + 0 + 0 = 0 \\\\ X[3] &= x[0]e^{-j0} + x[1]e^{-j3\\pi/2} + 0e^{-j3\\pi} + 0e^{-j9\\pi/2}\\\\ &= 1 + (j) + 0 + 0 = 1+j = \\sqrt{2} e^{j\\pi/4} \\end{align*}',13,10,9);
