INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10748,'equation','eq:Xk-for-vowel','X[k]= \\begin{cases} 0.0236(40) e^{j1.5077}&\\quad  k=2 \\\\ 0.0681(40) e^{j1.8769}&\\quad  k=4 \\\\ 0.2442(40) e^{-j0.1852}&\\quad  k=5 \\\\ 0.1471(40) e^{-j1.4488}&\\quad  k=16 \\\\ 0.06115(40)&\\quad  k=17 \\end{cases}',13,12,6);
