INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10759,'paragraph',NULL,'The discrete-time signal <span class="ITS_HTML">x[n]</span> defined in <a href="eq:perexample" class="ITS_reference">&raquo;</a> is plotted in Fig. <a href="fig:perspec2" class="ITS_reference">&raquo;</a>(a), from which we can see its periodicity <span class="ITS_HTML">x[n]=x[n+40]</span>. Now, if one cycle of <span class="ITS_HTML">x[n]</span> from Fig. <a href="fig:perspec2" class="ITS_reference">&raquo;</a>(a) is used in  a 40-point DFT <a href="eq:dft3" class="ITS_reference">&raquo;</a>, we obtain the 40 DFT  coefficients <span class="ITS_HTML">X[k]</span> shown as magnitude and phase in Figs. <a href="fig:perspec2" class="ITS_reference">&raquo;</a>(b) and (c), respectively. These 40 DFT coefficients represent the sequence <span class="ITS_HTML">x[n]</span> <i> exactly</i> through the synthesis formula of the IDFT <a href="eq:idft3" class="ITS_reference">&raquo;</a>.','13','12','7');
