INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10764,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Show that the IDFT of <span class="ITS_HTML">X[k]</span> defined in Exercise <a href="exer:IDFT-defined" class="ITS_reference">&raquo;</a> gives <span class="ITS_HTML">x[n]</span> in <a href="eq:perexample" class="ITS_reference">&raquo;</a>. Furthermore, show that the signal <span class="ITS_HTML">x[n]</span> obtained via the IDFT is periodic with period <span class="ITS_HTML">N</span>. </div>','13','12','8');
