INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10947,'image','Chapter13/art/sines_specgram','Resolution test for digital spectrum analysis using two different window lengths: (a) $L=512$, which is 128 msec at $\fs = 4000$ Hz; (b) $L = 128$, or 32 msec. The FFT length $N$ is equal to $L$.',13,13,34);
