INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10953,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch13/Fig13-26_sines_specgram.png" alt="FILES/SP1Figures/Ch13/Fig13-26_sines_specgram.png"></center><br><div class="ITS_Caption">Resolution test for digital spectrum analysis using two different window lengths: (a) <span class="ITS_HTML">L=512</span>, which is 128 msec at <span class="ITS_HTML">&fnof;<sub>s</sub> = 4000</span> Hz; (b) <span class="ITS_HTML">L = 128</span>, or 32 msec. The FFT length <span class="ITS_HTML">N</span> is equal to <span class="ITS_HTML">L</span>.</span></div></div>','13','13','34');
