INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(132,'footnote',NULL,'It is also common to refer to cosine or sine signals as cosine or sine <i>waves</i>INDEX#Cosine waves#INDEX, particularly when referring to acoustic or electrical signals.',2,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(133,'chapter',NULL,'Sinusoids',2,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(134,'footnote',NULL,'It is also common to refer to cosine or sine signals as cosine or sine <i>waves</i>INDEX#Cosine waves#INDEX, particularly when referring to acoustic or electrical signals.',2,2,1);
