INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(195,'table','tab:trig-props','\   \begin{tabular}{|l||l|} \hline \multicolumn{1}{|c||}{Property} & \multicolumn{1}{c|}{Equation} \\ \hline\hline Equivalence & $ \sin\theta = \cos( \theta -\pi/2)\mbox{ or }\cos(\theta)= \sin(\theta+\pi/2)$ \\ \hline Periodicity & $\cos(\theta + 2\pi k) = \cos\theta $, when $k$ is an integer \\ \hline Evenness of cosine & $\cos(-\theta) = \cos\theta $ \\ \hline Oddness of sine &$ \sin(-\theta) = - \sin\theta $ \\ \hline Zeros of sine & $ \sin(\pi k) = 0$, when $k $ is an integer \\ \hline Ones of cosine & $ \cos(2\pi k) = 1$,  when  $k$ is an integer. \\ \hline \vrule height 12pt depth6pt width 0pt Minus ones of cosine & $\cos [2\pi(k+\half)] = -1$, when  $k$ is an integer. \\ \hline \end{tabular}',2,4,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(196,'table','','\\begin{tabular}{|l||l|} \\hline \\multicolumn{1}{|c||}{Property} & \\multicolumn{1}{c|}{Equation} \\\\ \\hline\\hline Equivalence &  \\sin\\theta = \\cos( \\theta -\\pi/2)\\mbox{ or }\\cos(\\theta)= \\sin(\\theta+\\pi/2) \\\\ \\hline Periodicity & \\cos(\\theta + 2\\pi k) = \\cos\\theta , when k is an integer \\\\ \\hline Evenness of cosine & \\cos(-\\theta) = \\cos\\theta  \\\\ \\hline Oddness of sine & \\sin(-\\theta) = - \\sin\\theta  \\\\ \\hline Zeros of sine &  \\sin(\\pi k) = 0, when k  is an integer \\\\ \\hline Ones of cosine &  \\cos(2\\pi k) = 1,  when  k is an integer. \\\\ \\hline \\vrule height 12pt depth6pt width 0pt Minus ones of cosine & \\cos [2\\pi(k+\\frac{1}{2})] = -1, when  k is an integer. \\\\ \\hline \\end{tabular}',2,4,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(197,'paragraph',NULL,'<div class="ITS_TABLE"><center>\   <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.exe?\\begin{tabular}{|l||l|} \\hline \\multicolumn{1}{|c||}{Property} & \\multicolumn{1}{c|}{Equation} \\\\ \\hline\\hline Equivalence &  \\sin\\theta = \\cos( \\theta -\\pi/2)\\mbox{ or }\\cos(\\theta)= \\sin(\\theta+\\pi/2) \\\\ \\hline Periodicity & \\cos(\\theta + 2\\pi k) = \\cos\\theta , when k is an integer \\\\ \\hline Evenness of cosine & \\cos(-\\theta) = \\cos\\theta  \\\\ \\hline Oddness of sine & \\sin(-\\theta) = - \\sin\\theta  \\\\ \\hline Zeros of sine &  \\sin(\\pi k) = 0, when k  is an integer \\\\ \\hline Ones of cosine &  \\cos(2\\pi k) = 1,  when  k is an integer. \\\\ \\hline \\vrule height 12pt depth6pt width 0pt Minus ones of cosine & \\cos [2\\pi(k+\\frac{1}{2})] = -1, when  k is an integer. \\\\ \\hline \\end{tabular}"/></div></center><br><div class="ITS_Caption">Basic properties of the sine and cosine functions.</span></div></div>',2,4,5);
