INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(222,'table','','\\begin{tabular}{|c||l|} \\hline \\multicolumn{1}{|c||}{Number} & \\multicolumn{1}{c|}{Equation} \\\\ \\hline\\hline \\vrule height 12pt depth 0pt width 0pt 1 & \\sin^2\\theta + \\cos^2\\theta = 1  \\\\ \\hline \\vrule height 12pt depth 0pt width 0pt 2 & \\cos 2\\theta =  \\cos^2\\theta - \\sin^2\\theta   \\\\ \\hline 3 & \\sin 2\\theta =2\\sin \\theta \\cos \\theta \\\\ \\hline 4 & \\sin (\\alpha\\pm\\beta) = \\sin \\alpha \\cos \\beta \\pm \\cos \\alpha \\sin \\beta \\\\ \\hline 5 &  \\cos (\\alpha \\pm \\beta) = \\cos \\alpha \\cos \\beta \\mp \\sin \\alpha  \\sin \\beta \\\\ \\hline \\end{tabular}',2,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(223,'table','tab:identities','[!t] \   <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.exe?\\begin{tabular}{|c||l|} \\hline \\multicolumn{1}{|c||}{Number} & \\multicolumn{1}{c|}{Equation} \\\\ \\hline\\hline \\vrule height 12pt depth 0pt width 0pt 1 & \\sin^2\\theta + \\cos^2\\theta = 1  \\\\ \\hline \\vrule height 12pt depth 0pt width 0pt 2 & \\cos 2\\theta =  \\cos^2\\theta - \\sin^2\\theta   \\\\ \\hline 3 & \\sin 2\\theta =2\\sin \\theta \\cos \\theta \\\\ \\hline 4 & \\sin (\\alpha\\pm\\beta) = \\sin \\alpha \\cos \\beta \\pm \\cos \\alpha \\sin \\beta \\\\ \\hline 5 &  \\cos (\\alpha \\pm \\beta) = \\cos \\alpha \\cos \\beta \\mp \\sin \\alpha  \\sin \\beta \\\\ \\hline \\end{tabular}"/></div>  <p>',2,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(224,'paragraph',NULL,'<div class="ITS_TABLE"><center>[!t] \   <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.exe?\\begin{tabular}{|c||l|} \\hline \\multicolumn{1}{|c||}{Number} & \\multicolumn{1}{c|}{Equation} \\\\ \\hline\\hline \\vrule height 12pt depth 0pt width 0pt 1 & \\sin^2\\theta + \\cos^2\\theta = 1  \\\\ \\hline \\vrule height 12pt depth 0pt width 0pt 2 & \\cos 2\\theta =  \\cos^2\\theta - \\sin^2\\theta   \\\\ \\hline 3 & \\sin 2\\theta =2\\sin \\theta \\cos \\theta \\\\ \\hline 4 & \\sin (\\alpha\\pm\\beta) = \\sin \\alpha \\cos \\beta \\pm \\cos \\alpha \\sin \\beta \\\\ \\hline 5 &  \\cos (\\alpha \\pm \\beta) = \\cos \\alpha \\cos \\beta \\mp \\sin \\alpha  \\sin \\beta \\\\ \\hline \\end{tabular}"/></div>  <p></center><br><div class="ITS_Caption">Some basic trigonometric identities.</span></div></div>',2,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(225,'equation','none',' \\frac{d \\sin \\theta}{d \\theta}= \\cos \\theta \\quad \\mbox{and} \\quad \\frac{d \\cos \\theta}{d \\theta}= -\\sin \\theta ',2,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(226,'math',NULL,'\\alpha=\\beta=\\theta',2,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(227,'equation','eq:ZZZZ-7','\\begin{eqnarray*} \\cos^2 \\theta &=&\\frac{1}{2}\\left(1+\\cos 2\\theta\\right)\\\\ \\sin^2 \\theta &=&\\frac{1}{2}\\left(1-\\cos 2\\theta\\right)   \\end{eqnarray*}',2,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(228,'paragraph',NULL,'Clearly, the sine and cosine functions are very closely related. This often leads to opportunities for simplification of expressions involving both sine and cosine functions.  In calculus, we have the interesting property that the sine and cosine functions are derivatives of each other: <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.exe? \\frac{d \\sin \\theta}{d \\theta}= \\cos \\theta \\quad \\mbox{and} \\quad \\frac{d \\cos \\theta}{d \\theta}= -\\sin \\theta "/></div> That is, the cosine function gives the slope of the sine function, and the sine function is the negative of the slope of the cosine function. In trigonometry, there are many  identities that can be used in simplifying expressions involving combinations of sinusoidal signals. Table <a href="tab:identities" class="ITS_reference">&raquo;</a> gives a brief table of <i>trigonometric identities</i> \index{Trigonometric identities} that will be useful. Recall from your study of trigonometry that these identities are not independent; e.g., identity 3 can be obtained from identity 4 by substituting <span class="ITS_HTML">&alpha;=&beta;=&theta;</span>. Also, these identities can be combined to derive other  identities. For example, combining identity 1 with identity 2 leads to the identities <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.exe?\\begin{eqnarray*} \\cos^2 \\theta &=&\\frac{1}{2}\\left(1+\\cos 2\\theta\\right)\\\\ \\sin^2 \\theta &=&\\frac{1}{2}\\left(1-\\cos 2\\theta\\right)   \\end{eqnarray*}"/></div> A more extensive table of trigonometric ident\-it\-ies may be found in any book on trigonometry or in a book of mathematical tables.',2,4,12);
