INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(327,'subsection','sec:Bhead-ZZZZ-34','Phase Shift and Time Shift',2,5,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(328,'subsection','sec:Bhead-ZZZZ-34','Phase Shift and Time Shift',2,5,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(329,'paragraph',NULL,'<h3 class="ITS_BOOK">2-3.3  Phase Shift and Time Shift</h3>  The phase shift  parameter <span class="ITS_HTML">&phi;</span> (together with the frequency) determines the time locations of the maxima and minima of a cosine wave. To be specific, notice that the sinusoid <a href="eq:general-cosine" class="ITS_reference">&raquo;</a> with <span class="ITS_HTML">&phi; = 0</span> has a positive peak at <span class="ITS_HTML">t=0</span>. When <span class="ITS_HTML">&phi; &ne; 0</span>, the phase shift determines how much the maximum of the cosine signal is shifted away from <span class="ITS_HTML">t=0</span>.',2,5,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(330,'equation','eq:triangle','s(t)= \\begin{cases} 2t     & 0\\leq t \\leq \\frac{1}{2}  \\\\  \\frac{1}{3}(4 - 2t)   &  \\frac{1}{2}\\leq t \\leq 2 \\\\  0              &  \\text{elsewhere} \\end{cases}',2,5,18);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(331,'equation','eq:shifted-triangle','x_1(t) = \\begin{cases} 2(t-2)       & 2 \\leq t \\leq 2\\frac{1}{2} \\\\  \\frac{1}{3}(8 - 2t)  & 2\\frac{1}{2} \\leq t \\leq 4 \\\\  0            & \\text{elsewhere} \\end{cases}',2,5,18);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(332,'equation','none',' 0 \\leq (t-2) \\leq 2 \\qquad\\Longrightarrow\\quad 2\\leq t \\leq 4. ',2,5,18);
