INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(439,'index',NULL,'Linear interpolation',2,6,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(440,'paragraph',NULL,'<div class="MATLAB"> n = -7:5<span class="ITS_HTML">1;<br> Ts = 0.005</span>1;<br> tn = n*Ts<span class="ITS_HTML">1;<br> xn = 20*cos(80*pi*tn - 0.4*pi)</span>1;<br> plot(tn,xn) </div>  would create a row vector  <tt>tn</tt> of 13 numbers between <span class="ITS_HTML">-0.035</span> and <span class="ITS_HTML">0.025</span> spaced by the sampling period 0.005, and a row vector  <tt>xn</tt> of samples of <span class="ITS_HTML">x(t)</span>. Then the MATLAB  function <tt>plot</tt> draws the corresponding points, connecting them with straight line segments. Constructing the curve between sample points in this way is called <i>linear interpolation</i>. The solid gray curve in the upper plot of Fig. <a href="fig:cosinefigs" class="ITS_reference">&raquo;</a> shows the result of linear interpolation when the sample spacing is <span class="ITS_HTML">T<sub>s</sub> = 0.005</span>. Intuitively, we realize that if the points are very close together, we will see a smooth curve. The important question is, &ldquo;How small must we make the sample spacing, so that the cosine signal can be accurately reconstructed between samples by linear interpolation?&rdquo; A qualitative answer to this question is provided by Fig. <a href="fig:cosinefigs" class="ITS_reference">&raquo;</a>, which shows plots produced by three different sampling periods.',2,6,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(441,'image','Chapter02/art/2f9} ','Sampled cosine signals $x(nT_s) =<br> 20\cos (2\pi(40) nT_s - 0.4\pi)$ for several values of $T_s$: (a)~$T_s=0.005$ sec; (b) $T_s =0.0025$ sec; (c) $T_s=0.0001$ sec.',2,6,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(442,'math',NULL,'x(nT_s) =<br> 20\\cos (2\\pi(40) nT_s - 0.4\\pi)',2,6,3);
