INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(566,'math',NULL,'x(t)=A\\cos(\\omega_0 t +\\phi)',2,7,30);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(567,'math',NULL,'X',2,7,30);
