INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(583,'math',NULL,'X',2,7,31);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(584,'math',NULL,'e^{j\\omega_0 t}',2,7,31);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(585,'math',NULL,'X',2,7,31);
