INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(600,'math',NULL,'\\phi',2,7,34);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(601,'math',NULL,'t=0',2,7,34);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(602,'math',NULL,'\\phi = \\pi/2',2,7,34);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(603,'math',NULL,'t=0',2,7,34);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(604,'math',NULL,'\\phi = 0',2,7,34);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(605,'math',NULL,'t=0',2,7,34);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(606,'paragraph',NULL,'A rotating phasor makes one complete revolution every time the angle <span class="ITS_HTML">&theta;(t)</span> changes by <span class="ITS_HTML">2&pi;</span> radians. The time it takes to make one revolution is also equal to the period, <span class="ITS_HTML">T<sub>0</sub></span>, of the complex exponential signal, so <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.exe? \\omega_0 T_0 = (2 \\pi f_0) T_0 = 2\\pi\\qquad\\Longrightarrow\\quad T_0 = \\frac{1}{f_0} "/></div> Notice that the phase shift <span class="ITS_HTML">&phi;</span> defines where the phasor is pointing when <span class="ITS_HTML">t=0</span>. For example, if <span class="ITS_HTML">&phi; = &pi;/2</span>, then the phasor is pointing  straight up when <span class="ITS_HTML">t=0</span>, whereas if <span class="ITS_HTML">&phi; = 0</span>, the phasor is pointing to the right when <span class="ITS_HTML">t=0</span>.',2,7,34);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(607,'equation','none',' z(t) =  e^{ j( t -\\pi/4)} ',2,7,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(608,'equation','none',' x(1.5\\pi) = \\mathb{R}\\{z(1.5\\pi)\\} = \\cos(1.5\\pi -\\pi/4) = -\\frac{\\sqrt{2}}{2}   %%%%\\cos(5\\pi/4) ',2,7,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(609,'math',NULL,'t=1.5\\pi',2,7,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(610,'math',NULL,'z(t)',2,7,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(611,'math',NULL,'t=1.5\\pi',2,7,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(612,'math',NULL,'t',2,7,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(613,'math',NULL,'z(t)',2,7,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(614,'math',NULL,'x(t)',2,7,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(615,'math',NULL,'0\\leq t\\leq 2\\pi',2,7,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(616,'paragraph',NULL,'The plots in Fig. <a href="fig:rot-phasors" class="ITS_reference">&raquo;</a>(a) illustrate the relationship between a single complex rotating phasor and the cosine signal waveform. The upper left plot shows the complex plane with two vectors. The vector  at an angle in the third quadrant represents the signal <div class="ITS_EQUATION"> z(t) =  e<sup> j( t -&pi;/4)</sup> </div> at the specific time <span class="ITS_HTML">t=1.5&pi;</span>. The horizontal vector pointing to the left  represents the real part of the vector <span class="ITS_HTML">z(t)</span> at the particular time <span class="ITS_HTML">t=1.5&pi;</span>; i.e., <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.exe? x(1.5\\pi) = \\mathb{R}\\{z(1.5\\pi)\\} = \\cos(1.5\\pi -\\pi/4) = -\\frac{\\sqrt{2}}{2}   %%%%\\cos(5\\pi/4) "/></div> As <span class="ITS_HTML">t</span> increases, the rotating phasor <span class="ITS_HTML">z(t)</span> rotates in the counterclockwise direction, and its real part <span class="ITS_HTML">x(t)</span> oscillates left and right along the real axis. This is shown in the lower left plot, which shows how the real part of the  rotating phasor has varied over one period, i.e., <span class="ITS_HTML">0&le; t&le; 2&pi;</span>.',2,7,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(617,'paragraph',NULL,'<h3 class="ITS_NOTE"><b>DEMO:</b> Rotating Phasors</h3>',2,7,36);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(618,'image','Chapter02/art/2f13} ','Rotating phasors: (a) single phasor rotating counterclockwise; (b) complex conjugate rotating phasors.',2,7,37);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(619,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch02/Fig02-13_rotating_phasors.png" alt="FILES/SP1Figures/Ch02/Fig02-13_rotating_phasors.png"></center><br><div class="ITS_Caption">Rotating phasors: (a) single phasor rotating counterclockwise; (b) complex conjugate rotating phasors.</span></div></div>',2,7,37);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(620,'equation','eq:inverse-euler-cos1','\\cos\\theta = \\frac{ e^{j \\theta} + e^{ -j \\theta}}{2}',2,7,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(621,'equation','eq:inverse-euler-sin1','\\sin\\theta = \\frac{ e^{ j \\theta}- e^{ -j\\theta}}{2j}',2,7,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(622,'subsection','sec:Bhead-ZZZZ-31','Inverse Euler Formulas',2,7,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(623,'subsection','sec:Bhead-ZZZZ-31','Inverse Euler Formulas',2,7,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(624,'index',NULL,'Inverse Euler formulas',2,7,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(625,'index',NULL,'Inverse Euler formula',2,7,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(626,'paragraph',NULL,'<h3 class="ITS_BOOK">2-5.7  Inverse Euler Formulas</h3>  The inverse Euler formulas allow us to write the cosine  function in terms of complex exponentials as <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.exe?\\cos\\theta = \\frac{ e^{j \\theta} + e^{ -j \\theta}}{2}"/></div> and also the sine function can be expressed as <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.exe?\\sin\\theta = \\frac{ e^{ j \\theta}- e^{ -j\\theta}}{2j}"/></div> (See Appendix <a href="appendix:A-complex-numbers" class="ITS_reference">&raquo;</a> for more details.)',2,7,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(627,'equation','none','\\begin{align*} A\\cos(\\omega_0 t + \\phi) &=  A\\left(\\frac{e^{ j(\\omega_0 t + \\phi)} +e^{ -j(\\omega_0 t + \\phi)}}{2} \\right)\\\\ &= \\frac{1}{2} X e^{  j\\omega_0 t } + \\frac{1}{2} X^\\ast  e^{ -j\\omega_0 t}\\\\ &= \\frac{1}{2} z(t) +\\frac{1}{2} z^\\ast (t)\\\\ &= \\mathb{R}\\{ z(t)\\} \\end{align*}',2,7,40);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(628,'math',NULL,'\\cos(\\omega_0 t + \\phi)',2,7,40);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(629,'math',NULL,'{}^\\ast ',2,7,40);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(630,'paragraph',NULL,'Equation <a href="eq:inverse-euler-cos1" class="ITS_reference">&raquo;</a>  can be used to express <span class="ITS_HTML">cos(&omega;<sub>0</sub> t + &phi;)</span> in terms of a positive and a negative frequency complex exponential as follows: <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.exe?\\begin{align*} A\\cos(\\omega_0 t + \\phi) &=  A\\left(\\frac{e^{ j(\\omega_0 t + \\phi)} +e^{ -j(\\omega_0 t + \\phi)}}{2} \\right)\\\\ &= \\frac{1}{2} X e^{  j\\omega_0 t } + \\frac{1}{2} X^\\ast  e^{ -j\\omega_0 t}\\\\ &= \\frac{1}{2} z(t) +\\frac{1}{2} z^\\ast (t)\\\\ &= \\mathb{R}\\{ z(t)\\} \\end{align*}"/></div> where <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?{}^\\ast "/> denotes complex conjugation.',2,7,40);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(631,'math',NULL,'\\omega_0',2,7,41);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(632,'math',NULL,'(\\omega_0)',2,7,41);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(633,'math',NULL,'(-\\omega_0)',2,7,41);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(634,'math',NULL,'\\frac{1}{2} X=\\frac{1}{2} Ae^{j\\phi',2,7,41);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(635,'math',NULL,'\\frac{1}{2} X^\\ast =\\frac{1}{2} Ae^{-j\\phi',2,7,41);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(636,'paragraph',NULL,'This formula has an interesting interpretation. The real cosine signal with frequency <span class="ITS_HTML">&omega;<sub>0</sub></span>  is actually composed of two complex exponential signals; one with positive frequency <span class="ITS_HTML">(&omega;<sub>0</sub>)</span> and the other with negative frequency <span class="ITS_HTML">(-&omega;<sub>0</sub>)</span>. The complex amplitude of the positive frequency complex exponential signal is <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?\\frac{1}{2} X=\\frac{1}{2} Ae^{j\\phi"/>}, and the complex amplitude of the negative frequency complex exponential is <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?\\frac{1}{2} X^\\ast =\\frac{1}{2} Ae^{-j\\phi"/>}. In other words, the real cosine signal can be represented as the sum  of two complex rotating phasors that are complex conjugates of each other.',2,7,41);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(637,'paragraph',NULL,'<h3 class="ITS_NOTE"><b>DEMO:</b> Rotating Phasors</h3>',2,7,42);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(638,'math',NULL,'\\frac{1}{2} z(t)',2,7,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(639,'math',NULL,'t=1.5\\pi',2,7,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(640,'math',NULL,'t',2,7,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(641,'math',NULL,'\\frac{1}{2} z^\\ast (t)',2,7,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(642,'math',NULL,'t=1.5\\pi',2,7,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(643,'math',NULL,'t',2,7,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(644,'math',NULL,'\\frac{1}{2} z^\\ast (t)',2,7,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(645,'math',NULL,'\\cos(t-\\pi/4)',2,7,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(646,'math',NULL,'0\\leq t\\leq 2\\pi',2,7,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(647,'paragraph',NULL,'Figure <a href="fig:rot-phasors" class="ITS_reference">&raquo;</a>(b) illustrates how the sum of the two half-amplitude complex conjugate rotating phasors becomes the real cosine signal.  In this case, the vector at an angle in the third quadrant is the complex rotating phasor <span class="ITS_HTML">&frac12; z(t)</span> at time <span class="ITS_HTML">t=1.5&pi;</span>. As <span class="ITS_HTML">t</span> increases after that time,  the angle would increase in the counterclockwise direction. Similarly, the vector in the second quadrant (plotted with a light-orange line) is the complex rotating phasor <span class="ITS_HTML">&frac12; z<sup>*</sup> (t)</span> at time <span class="ITS_HTML">t=1.5&pi;</span>. As <span class="ITS_HTML">t</span> increases after that time, the angle of <span class="ITS_HTML">&frac12; z<sup>*</sup> (t)</span> will increase in the clockwise direction. The horizontal vector pointing to the right is the sum of these two complex conjugate rotating phasors. The result is the same as the real vector in the plot on the left, and therefore the real cosine wave traced out as a function of time is the same in both cases. The lower right shows the variation of the real values of <span class="ITS_HTML">cos(t-&pi;/4)</span> for <span class="ITS_HTML">0&le; t&le; 2&pi;</span>.',2,7,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(648,'paragraph',NULL,'This representation of  real sinusoidal signals in terms of their positive and negative frequency components is a remarkably useful concept. The negative frequencies, which arise due to the complex exponential representation, turn out to lead to many simplifications in the analysis of signal and systems problems. We will develop this representation further in Chapter <a href="chapter:three-spectrum" class="ITS_reference">&raquo;</a>, where we introduce the idea of the spectrum of a signal.',2,7,44);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(649,'equation','none',' A \\sin(\\omega_0 t + \\phi) = \\frac{1}{2} X e^{ -j\\pi/2} e^{ j\\omega_0 t } + \\frac{1}{2} X^\\ast  e^{ j\\pi/2} e^{ -j\\omega_0 t } ',2,7,45);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(650,'math',NULL,'X=Ae^{ j\\phi',2,7,45);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(651,'math',NULL,'\\mp \\pi/2',2,7,45);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(652,'math',NULL,'X',2,7,45);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(653,'math',NULL,'X^\\ast ',2,7,45);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(654,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div>      Show that the following representation can be derived for the real sine signal: <div class="ITS_EQUATION"> A sin(&omega;<sub>0</sub> t + &phi;) = &frac12; X e<sup> -j&pi;/2</sup> e<sup> j&omega;<sub>0</sub> t </sup> + &frac12; X<sup>*</sup>  e<sup> j&pi;/2</sup> e<sup> -j&omega;<sub>0</sub> t </sup> </div> where <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?X=Ae^{ j\\phi"/>}. In this  case, the interpretation is that the sine signal is also composed of two complex exponentials with the same positive and negative frequencies, but the complex coefficients multiplying the terms are different from those of the cosine signal. Specifically, the sine signal requires additional phase shifts of <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?\\mp \\pi/2"/> applied to the complex amplitude <span class="ITS_HTML">X</span> and <span class="ITS_HTML">X<sup>*</sup> </span>, respectively. </div>',2,7,45);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(655,'equation','eq:sum-cosines','\\sum_{k=1}^N A_k \\cos(\\omega_0 t +\\phi_k) = A \\cos(\\omega_0 t +\\phi)',2,8,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(656,'equation','eq:trigident','\\begin{align} A_k\\cos(\\omega_0 t + \\phi_k) = A_k &\\cos\\phi_k \\cos(\\omega_0 t)\\\\ &- A_k \\sin\\phi_k \\sin(\\omega_0 t)\\\\ \\end{align}',2,8,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(657,'math',NULL,'N',2,8,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(658,'math',NULL,'\\cos(\\omega_0 t)',2,8,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(659,'math',NULL,'\\sin(\\omega_0 t)',2,8,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(660,'section','sec:phasor-addition','Phasor Addition',2,8,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(661,'section','sec:phasor-addition','Phasor Addition',2,8,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(662,'index',NULL,'Phasor addition',2,8,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(663,'paragraph',NULL,'<h3 class="ITS_BOOK">2-6  Phasor Addition</h3>  There are many situations in which it is necessary to add two or more sinusoidal signals. When all signals have the same frequency, the problem simplifies. This problem arises in electrical circuit analysis, and it will arise again in Chapter <a href="chapter:five-FIR-conv" class="ITS_reference">&raquo;</a>, where we introduce the concept of discrete-time filtering. Thus it is useful to develop a mechanism for adding several sinusoids having the same frequency, <i>but with different amplitudes and phases.</i>  Our goal is to prove that the following statement is true: <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.exe?\\sum_{k=1}^N A_k \\cos(\\omega_0 t +\\phi_k) = A \\cos(\\omega_0 t +\\phi)"/></div> Equation <a href="eq:sum-cosines" class="ITS_reference">&raquo;</a> states that a sum of <span class="ITS_HTML">N</span> cosine signals of differing amplitudes and phase shifts, but with the same frequency, can always be reduced to a single cosine signal of the same frequency. A proof of  <a href="eq:sum-cosines" class="ITS_reference">&raquo;</a> can be accomplished by using trigonometric identities such as <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.exe?\\begin{align} A_k\\cos(\\omega_0 t + \\phi_k) = A_k &\\cos\\phi_k \\cos(\\omega_0 t)\\\\ &- A_k \\sin\\phi_k \\sin(\\omega_0 t)\\\\ \\end{align}"/></div> We can expand the sum <a href="eq:sum-cosines" class="ITS_reference">&raquo;</a> into sines and cosines, collect terms involving <span class="ITS_HTML">cos(&omega;<sub>0</sub> t)</span> and those involving <span class="ITS_HTML">sin(&omega;<sub>0</sub> t)</span>, and then finally use the same identity <a href="eq:trigident" class="ITS_reference">&raquo;</a> in the reverse direction. However, this is exceedingly tedious to do as a numerical computation (see Exercise <a href="exer:sum-sinusoids" class="ITS_reference">&raquo;</a>), and it leads to some very messy expressions if we wish to obtain a general formula. As we will see, a much simpler approach can be based on the complex exponential representation of the cosine signals.',2,8,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(664,'equation','none','\\begin{align*} A   &=  \\{[1.7 \\cos(70\\pi/180) + 1.9 \\cos(200\\pi/180)]^2 \\\\ & \\phantom{=} + [1.7 \\sin(70\\pi/180)  + 1.9 \\sin(200\\pi/180)]^2\\}^{1/2}\\\\ & = 1.532 \\end{align*}',2,8,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(665,'equation','none',' 1.7 \\cos(20\\pi t + 70\\pi/180)+ 1.9 \\cos(20\\pi t + 200\\pi/180) ',2,8,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(666,'math',NULL,' A\\cos(20\\pi t +\\phi)',2,8,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(667,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div>    Use <a href="eq:trigident" class="ITS_reference">&raquo;</a> to show that the sum <div class="ITS_EQUATION"> 1.7 cos(20&pi; t + 70&pi;/180)+ 1.9 cos(20&pi; t + 200&pi;/180) </div> reduces to <span class="ITS_HTML"> Acos(20&pi; t +&phi;)</span>, where <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.exe?\\begin{align*} A   &=  \\{[1.7 \\cos(70\\pi/180) + 1.9 \\cos(200\\pi/180)]^2 \\\\ & \\phantom{=} + [1.7 \\sin(70\\pi/180)  + 1.9 \\sin(200\\pi/180)]^2\\}^{1/2}\\\\ & = 1.532 \\end{align*}"/></div>',2,8,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(668,'equation','none','\\begin{align*} \\phi& =\\tan^{-1}\\left\\{ \\frac{1.7 \\sin(70\\pi/180) + 1.9 \\sin(200\\pi/180)} {1.7 \\cos(70\\pi/180) + 1.9 \\cos(200\\pi/180)} \\right\\} \\\\ &= 141.79\\pi/180 = 2.475 \\mbox{\\,rads} \\end{align*}',2,8,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(669,'math',NULL,'\\phi',2,8,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(670,'math',NULL,'141.79^\\circ',2,8,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(671,'paragraph',NULL,'and <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.exe?\\begin{align*} \\phi& =\\tan^{-1}\\left\\{ \\frac{1.7 \\sin(70\\pi/180) + 1.9 \\sin(200\\pi/180)} {1.7 \\cos(70\\pi/180) + 1.9 \\cos(200\\pi/180)} \\right\\} \\\\ &= 141.79\\pi/180 = 2.475 \\mbox{\\,rads} \\end{align*}"/></div> The value of <span class="ITS_HTML">&phi;</span>, given in radians, corresponds to <span class="ITS_HTML">141.79<sup><sup>o</sup></sup></span>. </div>',2,8,3);
