INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(695,'equation','eq:phasor-add-problem','\\begin{align} x(t) &= \\sum\\limits_{k=1}^N  A_k \\cos(\\omega_0 t +\\phi_k)\\\\ &= A\\cos(\\omega_0 t + \\phi) \\end{align}',2,8,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(696,'equation','eq:phasor-add','\\sum\\limits_{k=1}^N A_k e^{j\\phi_k} = A e^{j\\phi}',2,8,12);
