INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1111,'table','tab:complex-amps','\caption{Complex amplitudes for the periodic signal that approximates the vowel &ldquo;ah&rdquo;. The $a_k$ coefficients are given for positive indices $k$, but the values for negative $k$ are the conjugates, $a_{-k} = a_k^*$.  }  \newcommand{\PM}{}\newcommand{\MP}{-} <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.exe?\\begin{tabular}{|c|c|c|c|c|} \\hline  k</b> & {f_k (Hz)} & {a_k} &  Mag</b> &  Phase</b>\\\\ \\hline\\hline  1 & 100 &  0 & 0 &  0 \\\\   \\hline  2 & 200 &  386 + j6101 & 6\\,113 &    1.508\\\\   \\hline  3 & 300 &  0  & 0 & 0 \\\\   \\hline  4 & 400 &  -4433 +  j 14024& 14\\,708  &   1.877 \\\\   \\hline  5 & 500 & 24000 - j 4498 & 24\\,418 &   - 0.185 \\\\   \\hline  6 & 600 &  0 &  0 & 0 \\\\   \\hline \\vdots & \\vdots & \\vdots & \\vdots &  \\vdots \\\\   \\hline  15 & 1500 & 0 & 0 & 0\\\\   \\hline  16 & 1600 &  828 - j 6760& 6\\,811 & - 1.449 \\\\   \\hline  17 & 1700 & 2362 + j0 & 2\\,362 & 0 \\\\   \\hline \\hline \\end{tabular}"/></div>',3,5,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1112,'equation','eq:synvowel','\\begin{align} x(t) = 2 \\mathb{R} \\big\\{&a_2 e^{ j2\\pi 2f_0 t} + a_4 e^{ j2\\pi 4f_0 t} + a_5 e^{ j2\\pi 5f_0 t}\\\\ &+ a_{16} e^{ j2\\pi 16f_0 t} + a_{17} e^{ j2\\pi 17f_0 t}\\big\\}  \\end{align}',3,5,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1113,'math',NULL,'\\{a_{\\pm2},\\, a_{\\pm4},\\, a_{\\pm5},\\, a_{\\pm16},\\, a_{\\pm17} \\}',3,5,8);
