INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1163,'math',NULL,'x_h(t)',3,5,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1164,'math',NULL,'t=0',3,5,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1165,'math',NULL,'t=2',3,5,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1166,'math',NULL,'t=4',3,5,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1167,'footnote',NULL,'We will discuss the square wave spectrum later in Sec.~REFERENCE#sec:square-wave#REFERENCE.',3,5,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1168,'footnote',NULL,'We will discuss the square wave spectrum later in Sec.~REFERENCE#sec:square-wave#REFERENCE.',3,5,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1169,'paragraph',NULL,'With a simple example, we want to demonstrate  that periodicity is tied to harmonic frequencies. We can do this by taking a specific example. Consider the harmonic signal <span class="ITS_HTML">x<sub>h</sub>(t)</span> made up from the first, third, and fifth harmonics of a square waveWe will discuss the square wave spectrum later in Sec. <a href="sec:square-wave" class="ITS_reference">&raquo;</a>. with fundamental frequency <span class="ITS_HTML">f<sub>0</sub>=10</span> Hz: <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.exe? x_h(t)= 2\\cos(20\\pi t) - \\mbox{$\\frac{2}{3}$} \\cos(20\\pi(3) t) + \\mbox{$\\frac{2}{5}$} \\cos(20\\pi(5) t) "/></div> A plot of <span class="ITS_HTML">x<sub>h</sub>(t)</span> is shown in Fig. <a href="fig:strip-sq-wave" class="ITS_reference">&raquo;</a> using a &ldquo;strip chart&rdquo; format. The plot consists of three lines, each one containing 2 sec of the signal. The first line starts at <span class="ITS_HTML">t=0</span>, the second at <span class="ITS_HTML">t=2</span>, and the third at <span class="ITS_HTML">t=4</span>. This lets us view a long section of the signal, which in this case is clearly periodic, with period equal to 1/10 sec.',3,5,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1170,'paragraph',NULL,'<h3 class="ITS_NOTE"><b>DEMO:</b> Spectrograms of  Sounds: Square Wave</h3>',3,5,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1171,'equation','none',' x_2(t)= 2\\cos(20\\pi t) - \\mbox{$\\frac{2}{3}$} \\cos(20\\pi \\sqrt{8}t) + \\mbox{$\\frac{2}{5}$}\\cos(20\\pi \\sqrt{27} t) ',3,5,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1172,'math',NULL,'x_2(t)',3,5,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1173,'math',NULL,'x_2(t)',3,5,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1174,'paragraph',NULL,'Now we create a second signal that is just a slight perturbation from the first. Define <span class="ITS_HTML">x<sub>2</sub>(t)</span> to be the sum of three sinusoids: <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.exe? x_2(t)= 2\\cos(20\\pi t) - \\mbox{$\\frac{2}{3}$} \\cos(20\\pi \\sqrt{8}t) + \\mbox{$\\frac{2}{5}$}\\cos(20\\pi \\sqrt{27} t) "/></div> The amplitudes are the same, but the frequencies have been changed slightly. The plot of in Fig. <a href="fig:strip-irrat" class="ITS_reference">&raquo;</a> shows that <span class="ITS_HTML">x<sub>2</sub>(t)</span> is not periodic.',3,5,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1175,'image','Chapter03/art/strip-sq-wave_5in} ','Sum of cosine waves of harmonic frequencies.  The fundamental frequency of $x_h(t)$  is 10 Hz.',3,5,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1176,'math',NULL,'x_h(t)',3,5,19);
