INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1179,'math',NULL,'x_2(t)',3,5,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1180,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch03/Fig03-11_strip-irrat.png" alt="FILES/SP1Figures/Ch03/Fig03-11_strip-irrat.png"></center><br><div class="ITS_Caption">Sum of three cosine waves with nonharmonic frequencies, <span class="ITS_HTML">x<sub>2</sub>(t)</span>. No matter how hard you try, you cannot find an exact repetition in this signal.</span></div></div>',3,5,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1181,'math',NULL,'f_0=10',3,5,21);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1182,'math',NULL,'T_0=1/10',3,5,21);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1183,'math',NULL,'20\\sqrt{2=28.28\\dots \\approx 30',3,5,21);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1184,'math',NULL,'30\\sqrt{3 = 51.96\\dots \\approx 50',3,5,21);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1185,'paragraph',NULL,'The spectrum plots in Fig. <a href="fig:spect-irrat-sq-wave" class="ITS_reference">&raquo;</a> will help explain the difference between the signals in Figs. <a href="fig:strip-irrat" class="ITS_reference">&raquo;</a> and <a href="fig:strip-sq-wave" class="ITS_reference">&raquo;</a>. In Fig. <a href="fig:spect-irrat-sq-wave" class="ITS_reference">&raquo;</a>(a), the frequencies are integer multiples of a common frequency, <span class="ITS_HTML">f<sub>0</sub>=10</span> Hz, so  the waveform of Fig. <a href="fig:strip-sq-wave" class="ITS_reference">&raquo;</a> is periodic with period <span class="ITS_HTML">T<sub>0</sub>=1/10</span> sec.  The waveform of Fig. <a href="fig:strip-irrat" class="ITS_reference">&raquo;</a> is <i> nonperiodic</i>. We can justify this in the &ldquo;frequency domain&rdquo; by examining Fig. <a href="fig:spect-irrat-sq-wave" class="ITS_reference">&raquo;</a>(b), which shows that the spectrum of the signal in Figs. <a href="fig:strip-irrat" class="ITS_reference">&raquo;</a> does not have a fundamental frequency, since the frequencies are not integer multiples of a common fundamental frequency.  These <span class="ITS_blue">spectrum</span> plots show &ldquo;how much&rdquo; of each cosine wave is in the sum, and they are very similar. However, the frequencies are slightly different: <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?20\\sqrt{2=28.28\\dots \\approx 30"/>} and <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?30\\sqrt{3 = 51.96\\dots \\approx 50"/>}. These slight shifts of frequency make a dramatic difference in the time waveform.',3,5,21);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1186,'image','Chapter03/art/spectrumNonPeriodic_5in} ','Spectrum of (a) the harmonic waveform in \protect{Fig. REFERENCE#fig:strip-sq-wave#REFERENCE',3,5,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1187,'protect','fig:spect-irrat-sq-wave','Fig. REFERENCE#fig:strip-irrat#REFERENCE that is not periodic.',3,5,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1188,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch03/Fig03-12_spectrumNonPeriodic.png" alt="FILES/SP1Figures/Ch03/Fig03-12_spectrumNonPeriodic.png"></center><br><div class="ITS_Caption">Spectrum of (a) the harmonic waveform in Fig. <a href="fig:strip-sq-wave" class="ITS_reference">&raquo;</a></span></div></div> which has a period of 0.1 sec, and (b) the nonharmonic waveform in <a href="fig:strip-irrat" class="ITS_reference">Fig. <a href="fig:strip-irrat" class="ITS_reference">&raquo;</a> that is not periodic.</a>',3,5,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1189,'equation','eq:synthesis-sum-ak','x(t) =  \\sum_{k=-\\infty}^{\\infty} a_k\\, e^{ j(2\\pi k /T_0) t}',3,6,1);
