INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1411,'image','Chapter03/art/spectrumSqWave} ','Spectrum of a square wave derived from its Fourier Series coefficients. Only the range from <span class="ITS_HTML">k=-13</span> to <span class="ITS_HTML">+13</span>   is shown. With a fundamental frequency of 25 Hz, this corresponds to frequencies ranging from <span class="ITS_HTML">-325</span> Hz to <span class="ITS_HTML">+325</span> Hz.',3,8,13);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1412,'paragraph',NULL,'<h3 class="ITS_BOOK">3-6.3  Spectrum for a Square Wave</h3> Figure <a href="fig:square-wave-spectrum" class="ITS_reference">&raquo;</a> shows the spectrum for the 50<tt>%</tt> duty cycle square wave analyzed in <a href="eq:FS-square-wave" class="ITS_reference">&raquo;</a> when the fundamental frequency is 25 Hz. <div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch03/Fig03-16_spectrumSqWave.png" alt="FILES/SP1Figures/Ch03/Fig03-16_spectrumSqWave.png"></center><br><div class="ITS_Caption">Spectrum of a square wave derived from its Fourier Series coefficients. Only the range from <span class="ITS_HTML">k=-13</span> to <span class="ITS_HTML">+13</span>   is shown. With a fundamental frequency of 25 Hz, this corresponds to frequencies ranging from <span class="ITS_HTML">-325</span> Hz to <span class="ITS_HTML">+325</span> Hz.</span></div></div> Since <span class="ITS_HTML">a<sub>k</sub> = 0</span> for <span class="ITS_HTML">k</span> nonzero and even, the  only frequencies present in the spectrum are  the odd harmonics   at <span class="ITS_HTML">&plusmn; 25</span>, <span class="ITS_HTML">&plusmn; 75</span>, <span class="ITS_HTML">&plusmn; 125</span>,  and so on. The complex amplitudes of the odd harmonics are the Fourier Series coefficients, <span class="ITS_HTML">a<sub>k</sub> = -j/(&pi; k)</span>, and these are used as the labels on the spectrum lines in Fig. <a href="fig:square-wave-spectrum" class="ITS_reference">&raquo;</a>. Also the figure shows that the  magnitude of these coefficients drops off as <span class="ITS_HTML">1/k</span>.',3,8,13);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1413,'paragraph',NULL,'<h3 class="ITS_NOTE"><b>DEMO:</b> Spectrogram of Simple Sounds</h3>',3,8,14);
