INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1896,'math',NULL,'y(t)',4,3,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1897,'math',NULL,'\\omega = 2\\pi f_0',4,3,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1898,'math',NULL,'\\omega = \\hat\\omega /T_s = \\hat\\omega f_s',4,3,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1899,'math',NULL,'\\omega',4,3,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1900,'math',NULL,'\\hat\\omega',4,3,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1901,'math',NULL,'-\\pi<\\hat\\omega \\leq \\pi',4,3,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1902,'math',NULL,'\\hat\\omega',4,3,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1903,'math',NULL,'-\\frac{1}{2}f_s',4,3,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1904,'math',NULL,'+\\frac{1}{2}f_s',4,3,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1905,'paragraph',NULL,'If the ideal C-to-D converter works correctly for a sampled cosine signal, then we can describe its operation as <span class="ITS_blue">frequency scaling.</span> For <span class="ITS_HTML">y[n]</span> above, the discrete-time frequency is <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?\\hat\\omega = 2\\pi f_0 T_s"/>, and the continuous-time frequency of <span class="ITS_HTML">y(t)</span> is <span class="ITS_HTML">&omega; = 2&pi;&fnof;<sub>0</sub></span>. Thus the relationship appears to be <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?\\omega = \\hat\\omega /T_s = \\hat\\omega f_s"/>.  In fact, we can confirm the same result if we solve the frequency scaling equation <a href="eq:norm-rad-freq" class="ITS_reference">&raquo;</a> for <span class="ITS_HTML">&omega;</span> in terms of <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?\\hat\\omega"/>, <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.exe?\\omega = \\hat\\omega f_s"/></div> Thus each frequency component in a discrete-time signal could be mapped to a frequency component of the continuous-time output signal. But there is one more issue: the discrete-time signal has aliases&mdash;an infinite number of them given by <a href="eq:alias-frequencies" class="ITS_reference">&raquo;</a>. Which discrete-time frequency will be used in <a href="eq:D2C-frequency-scaling" class="ITS_reference">&raquo;</a>? The selection rule is arbitrary, but the ideal C-to-D converter always selects the lowest possible frequency components (the principal aliases). These  frequencies are guaranteed to lie in the range <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?-\\pi<\\hat\\omega \\leq \\pi"/>, so when converting from <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?\\hat\\omega"/> to analog frequency, the output frequency <font color="blue">always lies between <span class="ITS_HTML">-&frac12;&fnof;<sub>s</sub></span> and <span class="ITS_HTML">+&frac12;&fnof;<sub>s</sub></span>.</font>',4,3,39);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1906,'paragraph',NULL,'<h3 class="ITS_NOTE"><b>DEMO:</b> Continuous-Discrete Sampling</h3>',4,3,40);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1907,'image','Chapter04/art/4f24} ','Sampling and reconstruction system.',4,3,42);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1908,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch04/Fig04-07_C2D_D2C_block_diagram.png" alt="FILES/SP1Figures/Ch04/Fig04-07_C2D_D2C_block_diagram.png"></center><br><div class="ITS_Caption">Sampling and reconstruction system.</span></div></div>',4,3,42);
