INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1913,'math',NULL,'x(t)',4,3,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1914,'paragraph',NULL,'In summary, the Shannon sampling theorem guarantees that if <span class="ITS_HTML">x(t)</span> contains no frequencies higher than <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?f_\\mbox{ max}"/>, and if <img class="ITS_TEX" src="/cgi-bin/mimetex.exe?f_s>2f_\\mbox{ max}"/>, then the output signal <span class="ITS_HTML">y(t)</span> of the ideal D-to-C converter is <i> equal</i> to the signal <span class="ITS_HTML">x(t)</span>, the input to the ideal C-to-D converter in Fig. <a href="fig:CtoD-then-DtoC" class="ITS_reference">&raquo;</a>.',4,3,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1915,'paragraph',NULL,'\iffalse',4,3,45);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1916,'equation','none','\\begin{align*} y[n] = y(n T_s) &=  A\\cos(2\\pi (f_0 + \\ell  f_s) n T_s + \\phi)\\\\ &=  A\\cos(2\\pi f_0 n T_s + 2\\pi \\ell n f_s T_s + \\phi) \\\\ &=  A\\cos(2\\pi f_0 n T_s + 2\\pi \\ell n  + \\phi)\\\\ &=  A\\cos(2\\pi f_0 n T_s + \\phi) \\\\ &=  x[n] \\end{align*}',4,3,46);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1917,'equation','eq:sinewave','x(t)=A\\cos(2\\pi f_0 t + \\phi)',4,3,46);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1918,'equation','eq:sampsine','x[n]=x(n T_s) = A\\cos(2\\pi f_0 n T_s + \\phi)',4,3,46);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1919,'equation','none',' y(t) = A\\cos(2\\pi (f_0+ \\ell  f_s) t + \\phi) ',4,3,46);
