INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1937,'math',NULL,'x[n]',4,3,47);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1938,'math',NULL,'f_0+\\ell f_s',4,3,47);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1939,'math',NULL,'f_0',4,3,47);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1940,'math',NULL,'f_s',4,3,47);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1941,'math',NULL,'f_s',4,3,47);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1942,'paragraph',NULL,'In other words,  <span class="ITS_HTML">y[n]</span> has the same sample values as <span class="ITS_HTML">x[n]</span>, so it is indistinguishable from <span class="ITS_HTML">x[n]</span>. Since <span class="ITS_HTML">l</span> was specified only to be an integer (either positive or negative), this means that there are an infinite number of sinusoids that will give the same sequence of samples as <span class="ITS_HTML">x[n]</span>. The frequencies <span class="ITS_HTML">&fnof;<sub>0</sub>+l &fnof;<sub>s</sub></span> are called <i> aliases</i> of the frequency <span class="ITS_HTML">&fnof;<sub>0</sub></span> with respect to the sampling frequency <span class="ITS_HTML">&fnof;<sub>s</sub></span>, because all of them appear to be the same when sampled at the rate <span class="ITS_HTML">&fnof;<sub>s</sub></span>.',4,3,47);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1943,'equation','none',' w(t)=A\\cos(2\\pi (-f_0+ \\ell f_s) t - \\phi) ',4,3,49);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1944,'equation','none',' \\begin{align} w[n] =  w(n T_s) &= A\\cos(2\\pi (-f_0 + \\ell f_s) n T_s - \\phi) \\cr &= A\\cos(-2\\pi f_0 n T_s + 2\\pi \\ell n f_s T_s - \\phi) \\cr &= A\\cos(-2\\pi f_0 n T_s + 2\\pi n \\ell - \\phi)\\cr &= A\\cos(2\\pi f_0 n T_s + \\phi) \\cr &= x[n]\\end{align} ',4,3,49);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1945,'math',NULL,'-f_0+ \\ell f_s',4,3,49);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1946,'math',NULL,'\\ell',4,3,49);
