INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2013,'math',NULL,'x(t)',4,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2014,'paragraph',NULL,'In summary, for the oversampling case  where the original frequency <span class="ITS_HTML">f<sub>0</sub></span> is less than <span class="ITS_HTML">&fnof;<sub>s</sub>/2</span>, the original  waveform will be reconstructed exactly. In the present example, <span class="ITS_HTML">f<sub>0</sub>=100</span> Hz and <span class="ITS_HTML">&fnof;<sub>s</sub>=500</span>, so the Nyquist condition of the sampling theorem is satisfied, and the output <span class="ITS_HTML">y(t)</span> equals the input <span class="ITS_HTML">x(t)</span> as shown in the middle panel of Fig. <a href="fig:samp3" class="ITS_reference">&raquo;</a>.',4,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2015,'equation','none',' \\begin{array}{ll} \\hat\\omega =  2.5\\pi + 2\\pi\\ell &\\qquad \\ell = 0, \\pm 1, \\pm 2, \\ldots\\\\ \\hat\\omega =  -2.5\\pi + 2\\pi\\ell &\\qquad \\ell = 0, \\pm 1, \\pm 2, \\ldots\\\\ \\end{array} ',4,4,14);
