INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2204,'equation','eq:aliased-fd','\\hat\\omega_{\\ell} = 2\\pi \\frac{-f_m }{f_s} + 2\\pi\\ell = -\\hat\\omega_{m} + 2\\pi\\ell',4,5,23);
