INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2259,'subsection','sec:Bhead-ZZZZ-71','Interpolation with Pulses',4,6,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2260,'subsection','sec:Bhead-ZZZZ-71','Interpolation with Pulses',4,6,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2261,'paragraph',NULL,'<h3 class="ITS_BOOK">4-4.1  Interpolation with Pulses</h3> How does the D-to-C converter work?  In this section, we explain how the D-to-C converter does interpolation, and then describe a practical system that is nearly the same as the ideal D-to-C converter. These actual hardware systems, called digital-to-analog (D-to-A) converters, approximate the behavior of the ideal D-to-C system.',4,6,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2262,'equation','eq:dtoc','y(t) =   \\sum_{n=-\\infty}^{\\infty} y[n] p(t-n T_s),',4,6,6);
