INSERT INTO dSPFirst (id,meta,name,content,chapter,section,paragraph) VALUES(225,'paragraph',NULL,'The received signal is the sum of the two copies, and since they travel different distances they have different time delays. If the transmitted signal is <span class="ITS_EQ">s(t)</span>, then the received signal<span class="ITS_keyword"> For simplicity we are ignoring propagation losses: When a radio signal propagates over a distance <span class="ITS_EQ">R</span>, its amplitude will be reduced by an amount that is  proportional to <span class="ITS_EQ">1/R^2</span>.</span> is x^2 + 10^6 (meters) (x-55)^2 + 10^6 + 55 (meters) \]',2,1,49);
