INSERT INTO dSPFirst (id,meta,name,content,chapter,section,paragraph) VALUES(344,'paragraph',NULL,'Clearly, the sine and cosine functions are very closely related. This often leads to opportunities for simplification of expressions involving both sine and cosine functions.  In calculus, we have the interesting property that the sine and cosine functions are derivatives of each other: d \sin \theta{d and d \cos \theta{d tab:identities gives a brief table of <i>trigonometric identities</i> <span class="ITS_index">Trigonometric identities</span> that will be useful. Recall from your study of trigonometry that these identities are not independent; e.g., identity 3 can be obtained from identity 4 by substituting <span class="ITS_EQ">\alpha=\beta=\theta</span>. Also, these identities can be combined to derive other{\parfillskip=0pt\par}',2,4,12);
INSERT INTO dSPFirst (id,meta,name,content,chapter,section,paragraph) VALUES(345,'cos^2 \theta &=&\half\left(1+\cos 2\theta\right)\\ \sin^2 \theta &=&\half\left(1-\cos 2\theta\right) LABEL#eq:ZZZZ-7#LABEL  \end','eq:ZZZZ-7','eqnarray*',2,4,14);
INSERT INTO dSPFirst (id,meta,name,content,chapter,section,paragraph) VALUES(346,'paragraph',NULL,'eqnarray* eqnarray* A more extensive table of trigonometric ident\-it\-ies may be found in any book on trigonometry or in a book of mathematical tables.',2,4,14);
