INSERT INTO dSPFirst (id,meta,name,content,chapter,section,paragraph) VALUES(353,'equation','eq:general-cosine','x(t)=A\cos(\omega_0t+\phi)=A\cos(2\pi f_0t+\phi)',2,5,1);
