INSERT INTO dSPFirst (id,meta,name,content,chapter,section,paragraph) VALUES(435,'subsection','sec:Bhead-ZZZZ-34','Phase Shift and Time Shift',2,5,17);
INSERT INTO dSPFirst (id,meta,name,content,chapter,section,paragraph) VALUES(436,'paragraph',NULL,'<h3 class="ITS_BOOK">2-5 Phase Shift and Time Shift</h3> Time shift The phase shift  parameter <span class="ITS_EQ">eq:general-cosine with <span class="ITS_EQ">\phi = 0</span> has a positive peak at <span class="ITS_EQ">t=0</span>. When <span class="ITS_EQ">\phi \neq 0</span>, the phase shift determines how much the maximum of the cosine signal is shifted away from <span class="ITS_EQ">t=0</span>.',2,5,17);
