INSERT INTO dSPFirst (id,meta,name,content,chapter,section,paragraph) VALUES(692,'paragraph',NULL,'A rotating phasor makes one complete revolution every time the angle <span class="ITS_EQ">1{f_0} <span class="ITS_EQ">t=0</span>. For example, if <span class="ITS_EQ">\phi = \pi/2</span>, then the phasor is pointing  straight up when <span class="ITS_EQ">t=0</span>, whereas if <span class="ITS_EQ">\phi = 0</span>, the phasor is pointing to the right when <span class="ITS_EQ">t=0</span>.',2,7,34);
